mapper.select:
select a.id, a.goods_sn, a.`name`, a.category_id, a.brand_id, a.gallery, a.keywords, a.goods_brief, a.is_on_sale, a.sort_order, a.counter_price, a.is_new, a.primary_pic_url, a.list_pic_url, a.is_hot, a.goods_unit, a.retail_price, a.add_time, a.deleted, a.start_date, a.end_date, a.integretion, a.person_num, a.content, a.memo, a.`status`, a.create_date, a.integretions, a.`state`, a.`type`, a.`sort_no`,a.act_pic_url, b.status orderStatus from litemall_goods a left join help_order b on a.id = b.goods_id and b.user_id = ${userId} where 1=1
and a.category_id=${categoryId}
and a.type = ${type}


select

  distinct


  ${column.value}

from litemall_goods




  order by ${example.orderByClause}


select

  ${column.value}

from litemall_goods




  order by ${example.orderByClause}

limit 1
